import style from './styles/CdrCaption.mjs';

/**
 * Cedar 2 component for captions
 * @version 0.0.1
 * @author [REI Software Engineering](https://rei.github.io/rei-cedar/)
 */

var CdrCaption = {
  name: 'CdrCaption',
  props: {
    /** Caption summary text */
    summary: String,

    /** Caption credit text */
    credit: String
  },
  data: function data() {
    return {
      style: style
    };
  },
  render: function render() {
    var h = arguments[0];
    return h("div", {
      "class": this.style['cdr-caption']
    }, [this.summary ? h("p", {
      "class": this.style['cdr-caption__summary']
    }, [this.summary]) : '', this.credit ? h("cite", {
      "class": this.style['cdr-caption__cite']
    }, [this.credit]) : '']);
  }
};

export default CdrCaption;
